import { useEffect, useState } from "react";
import styled from "styled-components";
import { AnimatePresence, LayoutGroup } from "framer-motion";

const backColor = {
    alimentation: "#85B36B;",
    hotellerie: "#4C83FA;",
    batiment: "#E76A4B;",
    commerce: "#827191;",
    metaux: "#245A6A;",
    mecanique: "#FC7F36;",
};

// Styled Components
const StyledWrapper = styled.div`
    width: 90%;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    margin: 3rem auto;

    @media (min-width: 1980px) {
        width: 50%;
    }
`;
const StyledCategories = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;

    h1 {
        margin: 15px 0 0 0;
        font-size: 2.5rem;
        font-weight: 900;
        color: black;
        text-align: center;
        text-transform: uppercase;
        background: rgb(132, 94, 194);
        background: linear-gradient(
            61deg,
            rgba(132, 94, 194, 1) 0%,
            rgba(214, 93, 177, 1) 18%,
            rgba(226, 98, 168, 1) 32%,
            rgba(255, 111, 145, 1) 47%,
            rgba(255, 150, 113, 1) 65%
        );
        background-clip: text;
        -webkit-background-clip: text;
        -webkit-text-fill-color: transparent;
    }

    p {
        width: 50%;
        font-size: 1.2rem;
        color: white;
        text-align: center;
    }

    @media (min-width: 768px) {
        p {
            width: 50%;
            font-size: 1.5rem;
        }
    }
`;

const Grid = styled.div`
    width: 90%;
    display: grid;
    grid-template-columns: repeat(1, auto);
    grid-template-rows: repeat(6, auto);
    grid-gap: 15px;
    justify-items: center;
    align-items: start;
    padding-top: 3.5rem;
    text-align: center;

    @media (min-width: 768px) {
        grid-template-columns: repeat(6, 1fr);
    }
`;

const Home = () => {
    const [jobs, setJobs] = useState([]);

    useEffect(() => {
        fetchJobs();
    }, []);

    async function fetchJobs() {
        const { data: metiers } = await supabase.from("metiers").select("*");
        setJobs(metiers);
    }

    return (
        <>
            <Navbar />
            <StyledWrapper>
                <StyledCategories>
                    <h1>FABRIK DES METIERS</h1>
                    <p>Découvre les métiers de l'Artisanat et leurs formations</p>
                </StyledCategories>
                <AnimatePresence>
                    <Grid>
                        <LayoutGroup>
                            
                        </LayoutGroup>
                    </Grid>
                </AnimatePresence>
            </StyledWrapper>
        </>
    );
};

export default Home;
